import { useState } from "react";
import { postApplication } from "../services/ApplicationService";

const ApplicationsForm = ({ addApplication }) => {
  const [formData, setFormData] = useState({});

  const onChange = (e) => {
    formData[e.target.id] = e.target.value;
    setFormData(formData);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    postApplication(formData).then(() => {
      addApplication(formData);
    });
  };

  return (
        <form onSubmit={onSubmit} id="applications-form">
            <h2>Add a New Application</h2>
            <div class="formWrap">
                <label for="name">Guest Name:</label>
                <input onChange={onChange} type="text" id="name" required/>
            </div>
            <div class="formWrap">
                <label for="email">Email:</label>
                <input onChange={onChange} type="text" id="email" required/>
            </div>
            <div class="dropdown">
                <label for="status">Checked in?:</label>
                <select onSelect={onChange} type="text" id="status">
                  <option value="true">Yes</option>
                  <option value="false">No</option>
                </select>
            </div>

            <input type="submit" value="Save" id="save" />
        </form>
  );
};

export default ApplicationsForm;
